const fs = require("fs");
const http = require("http");

var read_file_stream = fs.createReadStream("sample_streams.txt");

read_file_stream.on("data", function(result){
    console.log("result", result.toString());
})

var write_file_stream = fs.createWriteStream("write_sample_streams.txt");
read_file_stream.pipe(write_file_stream);